var _ = Describe("Santa should know:", func() {
	Describe("Puzzle questions", func() {

		Specify("first instruction that will get him on the basement", func() {
			_, actualChan := countFloorsAndGiveTheFirstCharThatBringsSantaOnDesiredFloor(givenMoveInstructions, -1)
			var actual int
			select {
			case position, ok := <-actualChan:
				if ok {
					actual = position
				} else {
					actual = neverTouchTheBasement
				}
			default:
				actual = neverTouchTheBasement
			}
			Expect(actual).To(Equal(1797))
		})

		Specify("how to follow floor travel instructions", func() {
			actual, _ := countFloorsAndGiveTheFirstCharThatBringsSantaOnDesiredFloor(givenMoveInstructions, 0)
			Expect(actual).To(Equal(280))
		})
	})
})
